Boolean  TTestAudioEngineApp::EventHandler(EventType*  pEvent)
{
	Boolean     bHandled = FALSE;

	switch(pEvent->eType)
	{
	case EVENT_AppLoad:
		{
			TMainForm *pWin = new TMainForm(this);
			if (pWin)
			{
				SetActiveWindow(pWin);
			}
			else
			{	// 窗口创建失败，退出应用。
				SendStopEvent();
			}
		}
		bHandled = TRUE;
		break;

	case EVENT_AppStopNotify:
		{
			
		}
		bHandled = FALSE;
		break;
	}
	if (FALSE == bHandled) 
	{
		return TApplication::EventHandler(pEvent);
	}

	return bHandled;
}
